import './App.css';
import React, { Component } from 'react';
import UserInput from './UserInput/UserInput';
import UserOutput from './UserOutput/UserOutput';

class App extends Component {

  state = {
    usernames: [
      {username: "xerrf"},
      {username: "sirjeebs"},
      {username: "journeyprime"}
    ]
  }

  overwriteUsernameHandler = (newUser) => {
    this.setState( {
      usernames: [
        {username: newUser},
        {username: newUser},
        {username: newUser}
      ]
    } )
  }

  render() {
    return (
      <div className="App">
        <UserInput onChange={this.overwriteUsernameHandler.bing(this, event.target.value)}/>
        <UserOutput username={this.state.usernames[0].username} />
        <UserOutput username={this.state.usernames[1].username} />
        <UserOutput username={this.state.usernames[2].username} />
      </div>
    );
  }
}

export default App;
