import './App.css';
import React, { Component } from 'react';

class App extends Component {

  state = {
    length: 0
  }

  getLength = (event) => {
    return event.target.value.length;
  }



  render() {
    const outputContainer = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center'
    }
    return (
      <div className="App">
        <input type="text"
        onChange={(event) => this.getLength(event)} />
      </div>
    );
  }
}

export default App;
