import './App.css';
import React, { Component } from 'react';
import ValidationComponent from './ValidationComponent/ValidationComponent';
import CharComponent from './CharComponent/CharComponent';

class App extends Component {

  state = {
    length: 0,
    word: ''
  }

  getLength = (event) => {
    this.setState( {
      length: event.target.value.length,
      word: event.target.value
    } );
  }

  render() {

    let charList = null;
    if (this.state.length > 0) {
      // For each character in word string
      let wordArray = this.state.word.split('');

      charList = wordArray.map( (char, index) => {
        // Assign a CharComponent
        console.log(char);
        return ( <CharComponent 
          letter={char}
          clicked={(event) => this.removeCharHanlder(event)} /> );
      });
    } else {charList = null;}

    return (
      <div className="App">
        <input type="text"
        onChange={(event) => this.getLength(event)} />
        <p>Length: {this.state.length}</p>
        <ValidationComponent length={this.state.length} />
        <div className="letters">{charList}</div>
      </div>
    );
  }
}

export default App;
