import React, {Component} from 'react'; // Class components
//import React, {useState} from 'react';  // Functional components with Hooks
import './App.css';
import Person from'./Person/Person';

// Class-based component

class App extends Component {
  state = {
    persons: [
      {name: "Bob", age: 23},
      {name: "Randers", age: 25},
      {name: "Stephanie", age: 22}
    ],
    otherState: "Other",
    showPersons: false
  }

  switchNameHandler = (newName) => {
      //console.log("Click");
      // DON'T DO THIS: this.state.persons[0].name = "Bobabillion"
      this.setState( {
        persons: [
          {name: newName, age: 23},
          {name: "Randers", age: 25},
          {name: "Stephanie", age: 24}
        ]
      } )
  }

  nameChangeHandler = (event) => {
    this.setState( {
      persons: [
        {name: "Bob", age:23},
        {name: event.target.value, age: 24},
        {name: "Stephanie", age:26}
      ]
    })
  }

  togglePersonHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  }

  render() {

    const style = {
      backgroundColor: 'white',
      font: 'inherit',
      border: 'solid blue thin',
      padding: '8px',
      cursor: 'pointer'
    };

    let persons = null;
    if(this.state.showPersons) {
      persons = (
        <div>
        {this.state.persons.map(person => {
          return <Person 
            name={person.name}
            age={person.age} />
        })}
        </div>
      );
    }

    return (
      <div className="App">
        <h1>Hello, I am a React app</h1>
        <button 
        style={style}
        onClick={this.togglePersonHandler} >Show People</button>
        {persons}

      </div>
    );
  }
}

export default App;
